import React, {Component} from 'react'
import './App.css'
import Header from "./components/Header";

class App extends Component {

    state = {
        buttons: [
            {name: 'Hello'},
            {name: 'World'},
            {name: 'Valera'},
            {name: 'Andrey'},
        ],
        heading: 'Lorem ipsum dolor sit amet.'
    };

    handleChangeHeading = () => {
        this.setState({ heading: 'Hello World Valera Andrey' })
    };

    shouldComponentUpdate(nextProps, nextState) {
        if (this.state.heading === nextState.heading) {
            return false
        }
    }

    render() {
        console.log('render');
        const { heading, buttons } = this.state;
        return (
            <div className={'app'}>
                <Header heading={heading} buttons={buttons} onChangeHeading={this.handleChangeHeading}/>
            </div>
        );
    }
}


export default App
